import * as DataType from '../types/inputData';
/**
 * The ResourceStorage interface declares the operations that all storage must
 * implement.
 */
export interface ResourceStorage {
    get: (keys: Array<string>) => Promise<Map<string, string | null>>;
    set: (data: {
        [key: string]: string;
    }) => Promise<{
        errorKeys: Array<string>;
    }>;
    getInfo: () => Promise<DataType.StorageInfoDataType>;
    remove: (keys: Array<string>) => Promise<{
        errorKeys: Array<string>;
    }>;
    clear: () => Promise<void>;
}
