
	import React from "react";

/**
 * @category Container
 * @subcategory App
 * @enum {string} App
 *
 */
export enum Theme {
    /** Theme dark */
    dark = "dark",
    /** Theme light */
    light = "light",
}

/**
 * @category Container
 * @subcategory App
 * @displayName App
 *
 */
export interface AppProps {
    /**
     * Tùy chỉnh theme cho các component trong App
     *
     * @type Theme
     */
    theme?: keyof typeof Theme;
    children?: React.ReactNode;
    id?: string;
}

	declare const App: React.FunctionComponent<AppProps>;

	export default App;
  