
	import React from "react";

/**
 * @category General
 * @subcategory Button
 * @enum {string}
 */
export const enum ButtonType {
    /** highlight */
    "highlight" = "highlight",
    /** danger */
    "danger" = "danger",
    /** neutral */
    "neutral" = "neutral",
}

/**
 * @category General
 * @subcategory Button
 * @enum {string}
 */
export const enum ButtonHTMLType {
    /** submit */
    "submit" = "submit",
    /** button */
    "button" = "button",
    /** reset */
    "reset" = "reset",
}

/**
 * @category General
 * @subcategory Button
 * @enum {string}
 */
export const enum ButtonSize {
    /** large */
    "large" = "large",
    /** medium */
    "medium" = "medium",
    /** small */
    "small" = "small",
}

/**
 * @category General
 * @subcategory Button
 * @enum {string}
 */
export const enum ButtonVariants {
    /** primary button */
    "primary" = "primary",
    /** secondary button */
    "secondary" = "secondary",
    /** tertiary button */
    "tertiary" = "tertiary",
}

export type NativeButtonProps = {
    htmlType?: keyof typeof ButtonHTMLType;
    onClick?: React.MouseEventHandler<HTMLElement>;
} & Omit<React.ButtonHTMLAttributes<unknown>, "type" | "onClick">;

/**
 * @category General
 * @subcategory Button
 * @displayName Button
 *
 */
export interface ButtonProps extends NativeButtonProps {
    className?: string;
    style?: React.CSSProperties;
    /**
     * Độ đậm của background color
     *
     * @type ButtonVariants
     * @defaultvalue primary
     */
    variant?: keyof typeof ButtonVariants;
    /** Hiển thị icon loading */
    loading?: boolean;
    /** Trạng thái disable */
    disabled?: boolean;
    children?: React.ReactNode;
    /**
     * Loại button
     *
     * @type ButtonType
     * @defaultvalue highlight
     */
    type?: keyof typeof ButtonType;
    /**
     * HTML button type
     *
     * @type ButtonHTMLType
     * @defaultvalue button
     */
    htmlType?: keyof typeof ButtonHTMLType;
    /**
     * Kích thước button
     *
     * @type ButtonSize
     */
    size?: keyof typeof ButtonSize;
    /** Đặt kích thước button full parent width */
    fullWidth?: boolean;
    /** Thêm prefix icon */
    prefixIcon?: React.ReactNode;
    /** Thêm suffix icon */
    suffixIcon?: React.ReactNode;
    /** Thêm icon cho button */
    icon?: React.ReactNode;
    ref?: React.MutableRefObject<HTMLButtonElement | null>;
}

	declare const Button: React.FunctionComponent<ButtonProps>;

	export default Button;
  