
	import React from "react";

/**
 * @category Data Display
 * @subcategory Progress
 * @displayName Progress
 *
 */
export interface ProgressProps {
    /**
     * Giá trị lớn nhất để hoàn thành tiến trình
     *  @defaultValue 100
     */
    maxCompleted?: number;
    /**
     * Giá trị hiện tại của tiến trình
     * @defaultValue 0
     */
    completed: number;
    /**
     * Định dạng hiển thị label, dùng khi  `showLabel={true}`
     * @function formatLabel
     */
    formatLabel?: (current: number, maxCompleted: number) => React.ReactNode;
    /**
     * Hiển thị label
     */
    showLabel?: boolean;
    /**
     * Độ rộng stroke tính theo px
     */
    strokeWidth?: number;
    /**
     * Màu sắc phần đã hoàn thành
     */
    strokeColor?: string;
    /**
     * Màu sắc phần còn lại
     */
    trailColor?: string;
    id?: string;
    className?: string;
}

	declare const Progress: React.FunctionComponent<ProgressProps>;

	export default Progress;
  