
	import { ComponentPropsWithoutRef } from "react";

/**
 * @category Data Entry
 * @subcategory Switch
 * @enum {string} SwitchSize
 *
 */
export const enum SwitchSize {
    /** Size small */
    small = "small",
    /** Size medium */
    medium = "medium",
}

/**
 * Bao gồm các props của Input trừ `ref`, `size`, `type`
 * @category Data Entry
 * @subcategory Switch
 * @displayName Switch
 *
 */
export interface SwitchProps
    extends Omit<ComponentPropsWithoutRef<"input">, "size" | "type"> {
    /** Thêm nhãn cho switch */
    label?: string;
    /**
     * Kích thước của switch
     *
     * @type SwitchSize
     */
    size?: SwitchSize;
    /** Thêm class name cho wrapper của switch */
    wrapperClassName?: string;
}

	declare const Switch: React.FunctionComponent<SwitchProps>;

	export default Switch;
  