import React from "react";

/**
 * @category Data Display
 * @subcategory Text
 * @enum {string} BodyTextSize
 */
export enum BodyTextSize {
    /** Size xLarge */
    xLarge = "xLarge",
    /** Size large */
    large = "large",
    /** Size normal */
    normal = "normal",
    /** Size small */
    small = "small",
    /** Size xSmall */
    xSmall = "xSmall",
    /** Size xxSmall */
    xxSmall = "xxSmall",
    /** Size xxxSmall */
    xxxSmall = "xxxSmall",
    /** Size xxxxSmall */
    xxxxSmall = "xxxxSmall",
}

/**
 * @category Data Display
 * @subcategory Text
 * @enum {string} TitleTextSize
 */
export enum TitleTextSize {
    /** Size xLarge */
    xLarge = "xLarge",
    /** Size large */
    large = "large",
    /** Size normal */
    normal = "normal",
    /** Size small */
    small = "small",
}

/**
 * @category Data Display
 * @subcategory Text
 * @enum {string} HeaderTextSize
 */
export enum HeaderTextSize {
    /** Size normal */
    normal = "normal",
    /** Size small */
    small = "small",
}

/**
 * @category Data Display
 * @subcategory Text
 * @displayName Text
 */
export interface BodyTextProps
    extends Omit<React.HTMLProps<HTMLSpanElement>, "size"> {
    /**
     * Text size
     * @type BodyTextSize
     */
    size?: keyof typeof BodyTextSize;
    style?: React.CSSProperties;
    className?: string;
    children?: React.ReactNode;
    /** Bold */
    bold?: boolean;
}

/**
 * @category Data Display
 * @subcategory Text
 * @displayName Text.Title
 */
export interface TitleTextProps
    extends Omit<React.HTMLProps<HTMLSpanElement>, "size"> {
    /**
     * Text size
     * @type TitleTextSize
     */
    size?: keyof typeof TitleTextSize;
    style?: React.CSSProperties;
    className?: string;
    children?: React.ReactNode;
}

/**
 * @category Data Display
 * @subcategory Text
 * @displayName Text.Header
 */
export interface HeaderTextProps
    extends Omit<React.HTMLProps<HTMLSpanElement>, "size"> {
    /**
     * Text size
     * @type HeaderTextSize
     */
    size?: keyof typeof HeaderTextSize;
    style?: React.CSSProperties;
    className?: string;
    children?: React.ReactNode;
}

export interface CompoundedComponent
    extends React.FunctionComponent<BodyTextProps> {
    Title: React.FunctionComponent<TitleTextProps>;
    Header: React.FunctionComponent<HeaderTextProps>;
}

declare const Text: CompoundedComponent;

export default Text;


